import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';
import { io } from 'socket.io-client';

/*export const socket = io("http://localhost:3000");*/
export const socket = io("http://localhost:3080");
/*export const socket = io("http://backend:3080");*/


const root = ReactDOM.createRoot(document.getElementById('root'));
const tiempoAsignacion = tarea.tiempoAsignacion
  ? `${(tarea.tiempoAsignacion / 60).toFixed(2)} minutos`
  : 'No asignado';

info.innerHTML = `
  <strong>${tarea.titulo}</strong> - ${tarea.estado}<br>
  Fecha límite: ${new Date(tarea.fechaLimite).toISOString().split('T')[0]}<br>
  Tiempo para asignar: <strong>${tiempoAsignacion}</strong>
`;
root.render(<App />);
